          Delimiter = ",",
          Columns = 9,
          Encoding = 65001,
          QuoteStyle = QuoteStyle.None
        ]
      ),
    #"Changed column type" =
      Table.TransformColumnTypes(
        Source, {
          {"Column1", type text},
          {"Column2", type text},
