    FlowRunData = "",
    Source =
      Csv.Document(
        Binary.Decompress(
          Binary.FromText(FlowRunData, BinaryEncoding.Base64),
          Compression.GZip
        ),
        [
          Delimiter = ",",
          Columns = 9,
          Encoding = 65001,
          QuoteStyle = QuoteStyle.None
        ]
      ),
    #"Changed column type" =
      Table.TransformColumnTypes(
        Source, {
          {"Column1", type text},
          {"Column2", type text},
          {"Column3", type text},
          {"Column4", type text},
          {"Column5", type text},
          {"Column6", type text},
          {"Column7", type text},
          {"Column8", type text},
          {"Column9", type text}
        }
      ),
    #"Removed top rows" =
      Table.Skip(#"Changed column type", 1),
    #"Promoted headers" =
      Table.PromoteHeaders(#"Removed top rows", [PromoteAllScalars = true]),
    #"Changed column type 1" =
      Table.TransformColumnTypes(
        #"Promoted headers", {
          {"startTime", type datetime},
          {"actionName", type text},
          {"flowRunId", type text},
          {"flowVersionId", Int64.Type},
          {"actionStatus", type text},
          {"actionStatusCode", type text},
          {"actionError", type text},
          {"actionId", type text},
          {"connectorId", type text}
        }
      ),
    #"Mapped to CDM" =
      Cdm.MapToEntity(
        #"Changed column type 1", {
          {"startTimestamp", "startTime", type datetime},
          {"activityName", "actionName", type text},
          {"caseId", "flowRunId", type text},
          {"flowVersionId", "flowVersionId"},
          {"actionStatus", "actionStatus"},
          {"actionStatusCode", "actionStatusCode"},
          {"actionError", "actionError"},
          {"actionId", "actionId"},
          {"connectorId", "connectorId"}
        },
        null,
        "Event log"
      ),
    #"Temp flowVersion table" =
      Table.Sort(
        Table.Buffer(
          Table.SelectColumns(
            #"Mapped to CDM",
            {"startTimestamp", "flowVersionId"}
          )
        ),
        {"startTimestamp"}
      ),
    #"Distinct flowVersionDate" =
      Table.Distinct(
        Table.RemoveColumns(
          Table.AddColumn(
            #"Temp flowVersion table",
            "flowVersionDate",
            each DateTime.ToText([startTimestamp], "MM/dd/yyyy")
          ),
          "startTimestamp"
        ),
        "flowVersionId"
      ),
    #"Expand flowVersion index" =
      Table.ExpandTableColumn(
        Table.AddColumn(
          Table.Group(
            #"Distinct flowVersionDate",
            "flowVersionDate",
            {{"GroupedData", each _, type table [ColumnToGroupBy=type, OtherColumns=type]}}
          ),
          "Index",
          each Table.AddIndexColumn([GroupedData], "Index", 1, 1)
        ),
        "Index",
        {"Index"},
        {"flowVersionIndex"}
      ),
    #"Merged flowVersion columns" =
      Table.RemoveColumns(
        Table.CombineColumns(
          Table.TransformColumnTypes(
            #"Expand flowVersion index",
            {{"flowVersionDate", type text},
            {"flowVersionIndex", type text}}
          ),
          {"flowVersionDate", "flowVersionIndex"},
          Combiner.CombineTextByDelimiter("-", QuoteStyle.None),
          "mergedFlowVersion"
        ),
        {"GroupedData"}
      ),
    #"Combined flowVersion columns" =
      Table.ReplaceValue(
        Table.RemoveColumns(
          Table.Join(
            Table.AddIndexColumn(#"Distinct flowVersionDate", "Index", 1, 1),
            "Index",
            Table.AddIndexColumn(#"Merged flowVersion columns", "Index", 1, 1),
            "Index"
          ),
          {"Index", "flowVersionDate"}
        ),
        "-1",
        "",
        Replacer.ReplaceText,
        {"mergedFlowVersion"}
      ),
    #"Join flowVersionDate" =
      Table.RenameColumns(
        Table.RemoveColumns(
          Table.Join(
            #"Mapped to CDM",
            "flowVersionId",
            #"Combined flowVersion columns",
            "flowVersionId"
          ),
          {"flowVersionId"}
        ),
        {"mergedFlowVersion", "flowVersionId"}
      ),
    #"Add startTimestampEpoch" =
      Table.TransformColumnTypes(
        Table.AddColumn(
          #"Join flowVersionDate",
          "startTimestamp.millisecondsEpoch",
          each Duration.TotalSeconds(
            DateTimeZone.From([startTimestamp]) - #datetimezone(1970, 1, 1, 0, 0, 0, 0, 0)
          ) * 1000
        ),
        {{"startTimestamp.millisecondsEpoch", Int64.Type}}
      ),
    #"Replaced value" = Table.ReplaceValue(#"Add startTimestampEpoch", "", " ", Replacer.ReplaceValue, {"actionStatusCode", "actionError", "actionId", "connectorId"})
  in
    #"Replaced value";
